'use client'

import React, { createContext, useContext, useState, useEffect } from 'react'
import { useCrud, type UseCrudReturn } from '@/hooks/use-crud'
import type { DataProvider, Schema, Field } from '@/lib/crudkit/data-provider'
import { cn } from '@/lib/utils'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { Textarea } from '@/components/ui/textarea'
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table'
import { Checkbox } from '@/components/ui/checkbox'
import { Badge } from '@/components/ui/badge'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Skeleton } from '@/components/ui/skeleton'

// ============================================
// CONTEXT
// ============================================

interface CrudContextValue extends UseCrudReturn {
  schema: Schema
}

const CrudContext = createContext<CrudContextValue | null>(null)

function useCrudContext() {
  const context = useContext(CrudContext)
  if (!context) {
    throw new Error('Crud compound components must be used within <Crud>')
  }
  return context
}

// ============================================
// MAIN CRUD COMPONENT
// ============================================

interface CrudProps {
  schema: Schema
  dataProvider: DataProvider
  children: React.ReactNode
}

function Crud({ schema, dataProvider, children }: CrudProps) {
  const crudReturn = useCrud(schema, dataProvider)

  return (
    <CrudContext.Provider value={{ ...crudReturn, schema }}>
      <div className={cn('crud-container', 'p-4')}>{children}</div>
    </CrudContext.Provider>
  )
}

// ============================================
// TOOLBAR COMPONENT
// ============================================

function CrudToolbar() {
  const { schema, state, actions } = useCrudContext()

  if (state.mode !== 'list') return null

  return (
    <div
      className={cn(
        'crud-toolbar',
        'mb-4 flex items-center justify-between'
      )}
    >
      <h1 className={cn('text-2xl font-bold')}>{schema.title} Management</h1>
      <div className={cn('flex gap-2')}>
        <Button
          onClick={() => actions.setMode('create')}
          variant="default"
        >
          + Create New
        </Button>
        <Button
          onClick={() => actions.refresh()}
          variant="outline"
        >
          ⟳ Refresh
        </Button>
      </div>
    </div>
  )
}

// ============================================
// FILTERS COMPONENT
// ============================================

interface CrudFiltersProps {
  filterFields?: Field[]
}

function CrudFilters({ filterFields }: CrudFiltersProps) {
  const { schema, state, actions } = useCrudContext()

  if (state.mode !== 'list') return null

  const fields = filterFields || schema.fields.filter((f) => f.filterable)

  if (fields.length === 0) return null

  return (
    <div className={cn('crud-filters', 'mb-4 rounded bg-muted p-4')}>
      <h3 className={cn('mb-2 font-semibold')}>Filters</h3>
      <div className={cn('flex flex-wrap gap-4')}>
        {fields.map((field) => (
          <div key={field.name} className="space-y-2">
            <Label htmlFor={`filter-${field.name}`}>
              {field.label}
            </Label>
            {field.type === 'select' ? (
              <Select
                value={state.filters[field.name] || ''}
                onValueChange={(value) =>
                  actions.setFilter(field.name, value || null)
                }
              >
                <SelectTrigger id={`filter-${field.name}`} className="w-[180px]">
                  <SelectValue placeholder="All" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="">All</SelectItem>
                  {field.options?.map((option) => (
                    <SelectItem key={option.value} value={option.value}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            ) : (
              <Input
                id={`filter-${field.name}`}
                type="text"
                value={state.filters[field.name] || ''}
                onChange={(e) =>
                  actions.setFilter(field.name, e.target.value || null)
                }
                className="w-[180px]"
              />
            )}
          </div>
        ))}
        <div className={cn('flex items-end')}>
          <Button
            onClick={() => actions.clearFilters()}
            variant="outline"
            size="sm"
          >
            Clear Filters
          </Button>
        </div>
      </div>
    </div>
  )
}

// ============================================
// LIST COMPONENT
// ============================================

interface CrudListProps {
  columns?: string[]
  showActions?: boolean
}

function CrudList({ columns, showActions = true }: CrudListProps) {
  const { schema, state, actions } = useCrudContext()

  if (state.mode !== 'list') return null

  const displayColumns = columns || schema.fields.map((f) => f.name)

  return (
    <div className={cn('crud-list')}>
      {/* Search Bar */}
      <div className={cn('mb-4 flex gap-2')}>
        <Input
          type="text"
          placeholder="Search..."
          value={state.search}
          onChange={(e) => actions.setSearch(e.target.value)}
          className="w-80"
        />
        {state.search && (
          <Button
            onClick={() => actions.setSearch('')}
            variant="outline"
          >
            Clear
          </Button>
        )}
      </div>

      {/* Bulk Actions */}
      {state.selectedRows.length > 0 && (
        <div className={cn('mb-4 rounded bg-muted p-3 flex items-center gap-4')}>
          <span>
            {state.selectedRows.length} items selected
          </span>
          <Button
            onClick={() => actions.deleteMany()}
            variant="destructive"
            size="sm"
          >
            Delete Selected
          </Button>
          <Button
            onClick={() => actions.selectRows([])}
            variant="ghost"
            size="sm"
          >
            Clear Selection
          </Button>
        </div>
      )}

      {/* Error Display */}
      {state.error && (
        <Alert variant="destructive" className="mb-4">
          <AlertDescription>{state.error}</AlertDescription>
        </Alert>
      )}

      {/* Table */}
      <Table>
        <TableHeader>
          <TableRow>
            <TableHead className="w-[50px]">
              <Checkbox
                checked={
                  state.selectedRows.length === state.data.length &&
                  state.data.length > 0
                }
                onCheckedChange={(checked) => {
                  if (checked) {
                    actions.selectRows(
                      state.data.map((item) => item[schema.idField])
                    )
                  } else {
                    actions.selectRows([])
                  }
                }}
              />
            </TableHead>
            {displayColumns.map((col) => {
              const field = schema.fields.find((f) => f.name === col)
              return (
                <TableHead
                  key={col}
                  className={cn(
                    field?.sortable !== false && 'cursor-pointer'
                  )}
                  onClick={() =>
                    field?.sortable !== false && actions.setSort(col)
                  }
                >
                  {field?.label || col}
                  {state.sortField === col && (
                    <span className={cn('ml-1')}>
                      {state.sortOrder === 'asc' ? '↑' : '↓'}
                    </span>
                  )}
                </TableHead>
              )
            })}
            {showActions && <TableHead>Actions</TableHead>}
          </TableRow>
        </TableHeader>
        <TableBody>
          {state.loading && (
            <TableRow>
              <TableCell
                colSpan={displayColumns.length + 2}
                className="h-24 text-center"
              >
                <div className="flex flex-col items-center gap-2">
                  <Skeleton className="h-4 w-[200px]" />
                  <Skeleton className="h-4 w-[150px]" />
                </div>
              </TableCell>
            </TableRow>
          )}
          {!state.loading && state.data.length === 0 && (
            <TableRow>
              <TableCell
                colSpan={displayColumns.length + 2}
                className="h-24 text-center text-muted-foreground"
              >
                No data found
              </TableCell>
            </TableRow>
          )}
          {!state.loading &&
            state.data.map((item) => (
              <TableRow key={item[schema.idField]}>
                <TableCell>
                  <Checkbox
                    checked={state.selectedRows.includes(item[schema.idField])}
                    onCheckedChange={(checked) => {
                      if (checked) {
                        actions.selectRows([
                          ...state.selectedRows,
                          item[schema.idField],
                        ])
                      } else {
                        actions.selectRows(
                          state.selectedRows.filter(
                            (id) => id !== item[schema.idField]
                          )
                        )
                      }
                    }}
                  />
                </TableCell>
                {displayColumns.map((col) => (
                  <TableCell key={col}>
                    {item[col]}
                  </TableCell>
                ))}
                {showActions && (
                  <TableCell>
                    <div className="flex gap-2">
                      <Button
                        onClick={() =>
                          actions.setMode('view', item[schema.idField])
                        }
                        variant="ghost"
                        size="sm"
                      >
                        View
                      </Button>
                      <Button
                        onClick={() =>
                          actions.setMode('edit', item[schema.idField])
                        }
                        variant="ghost"
                        size="sm"
                      >
                        Edit
                      </Button>
                      <Button
                        onClick={() => actions.delete(item[schema.idField])}
                        variant="ghost"
                        size="sm"
                        className="text-destructive hover:text-destructive"
                      >
                        Delete
                      </Button>
                    </div>
                  </TableCell>
                )}
              </TableRow>
            ))}
        </TableBody>
      </Table>

      {/* Pagination */}
      <div
        className={cn(
          'mt-4 flex items-center justify-between rounded bg-muted p-4'
        )}
      >
        <div className={cn('flex items-center gap-4')}>
          <span className="text-sm">
            Showing {Math.min((state.page - 1) * state.pageSize + 1, state.totalCount)} to{' '}
            {Math.min(state.page * state.pageSize, state.totalCount)} of{' '}
            {state.totalCount} items
          </span>
          <Select
            value={String(state.pageSize)}
            onValueChange={(value) => actions.setPageSize(Number(value))}
          >
            <SelectTrigger className="w-[140px]">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="5">5 per page</SelectItem>
              <SelectItem value="10">10 per page</SelectItem>
              <SelectItem value="25">25 per page</SelectItem>
              <SelectItem value="50">50 per page</SelectItem>
            </SelectContent>
          </Select>
        </div>
        <div className={cn('flex items-center gap-1')}>
          <Button
            onClick={() => actions.setPage(1)}
            disabled={state.page === 1}
            variant="outline"
            size="sm"
          >
            ⏮
          </Button>
          <Button
            onClick={() => actions.setPage(state.page - 1)}
            disabled={state.page === 1}
            variant="outline"
            size="sm"
          >
            ← Previous
          </Button>
          <Button
            onClick={() => actions.setPage(state.page + 1)}
            disabled={state.page * state.pageSize >= state.totalCount}
            variant="outline"
            size="sm"
          >
            Next →
          </Button>
          <Button
            onClick={() =>
              actions.setPage(Math.ceil(state.totalCount / state.pageSize))
            }
            disabled={state.page * state.pageSize >= state.totalCount}
            variant="outline"
            size="sm"
          >
            ⏭
          </Button>
        </div>
      </div>
    </div>
  )
}

// ============================================
// FORM COMPONENT
// ============================================

interface CrudFormProps {
  fields?: Field[]
}

function CrudForm({ fields }: CrudFormProps) {
  const { schema, state, actions } = useCrudContext()
  const [formData, setFormData] = useState<any>({})
  const [errors, setErrors] = useState<Record<string, string>>({})

  useEffect(() => {
    if (state.mode === 'edit' && state.currentItem) {
      setFormData(state.currentItem)
    } else if (state.mode === 'create') {
      setFormData({})
    }
  }, [state.mode, state.currentItem])

  if (!['create', 'edit'].includes(state.mode)) return null

  const displayFields =
    fields ||
    schema.fields.filter((f) =>
      state.mode === 'create' ? f.showOnCreate !== false : f.showOnEdit !== false
    )

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()

    // Validation
    const newErrors: Record<string, string> = {}
    displayFields.forEach((field) => {
      if (field.required && !formData[field.name]) {
        newErrors[field.name] = `${field.label} is required`
      }
    })

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors)
      return
    }

    try {
      await actions.save(formData)
      setFormData({})
      setErrors({})
    } catch (err) {
      // Error already handled in useCrud
    }
  }

  const handleChange = (field: string, value: any) => {
    setFormData((prev: any) => ({ ...prev, [field]: value }))
    setErrors((prev) => ({ ...prev, [field]: '' }))
  }

  return (
    <form onSubmit={handleSubmit} className={cn('max-w-2xl space-y-6')}>
      <h2 className={cn('text-xl font-bold')}>
        {state.mode === 'create' ? 'Create' : 'Edit'} {schema.title}
      </h2>

      {state.error && (
        <Alert variant="destructive">
          <AlertDescription>{state.error}</AlertDescription>
        </Alert>
      )}

      {displayFields.map((field) => (
        <div key={field.name} className="space-y-2">
          <Label htmlFor={`form-${field.name}`}>
            {field.label}
            {field.required && <span className="text-destructive"> *</span>}
          </Label>

          {field.type === 'select' ? (
            <Select
              value={formData[field.name] || ''}
              onValueChange={(value) => handleChange(field.name, value)}
            >
              <SelectTrigger id={`form-${field.name}`}>
                <SelectValue placeholder={`Select ${field.label}`} />
              </SelectTrigger>
              <SelectContent>
                {field.options?.map((option) => (
                  <SelectItem key={option.value} value={option.value}>
                    {option.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          ) : field.type === 'textarea' ? (
            <Textarea
              id={`form-${field.name}`}
              value={formData[field.name] || ''}
              onChange={(e) => handleChange(field.name, e.target.value)}
              rows={4}
            />
          ) : (
            <Input
              id={`form-${field.name}`}
              type={field.type || 'text'}
              value={formData[field.name] || ''}
              onChange={(e) => handleChange(field.name, e.target.value)}
            />
          )}

          {errors[field.name] && (
            <p className="text-sm text-destructive">
              {errors[field.name]}
            </p>
          )}
        </div>
      ))}

      <div className="flex gap-2">
        <Button
          type="submit"
          disabled={state.loading}
        >
          {state.loading ? 'Saving...' : 'Save'}
        </Button>
        <Button
          type="button"
          onClick={() => actions.setMode('list')}
          variant="outline"
        >
          Cancel
        </Button>
      </div>
    </form>
  )
}

// ============================================
// VIEW COMPONENT
// ============================================

function CrudView() {
  const { schema, state, actions } = useCrudContext()

  if (state.mode !== 'view' || !state.currentItem) return null

  return (
    <div className={cn('max-w-2xl space-y-6')}>
      <h2 className={cn('text-xl font-bold')}>
        View {schema.title}
      </h2>

      {state.loading && (
        <div className="space-y-3">
          {schema.fields.map((field) => (
            <div key={field.name} className="space-y-2">
              <Skeleton className="h-4 w-[100px]" />
              <Skeleton className="h-4 w-[250px]" />
            </div>
          ))}
        </div>
      )}

      {!state.loading && state.currentItem && (
        <div className="space-y-3">
          {schema.fields.map((field) => (
            <div key={field.name}>
              <strong className="font-medium">{field.label}:</strong>{' '}
              <span className="text-muted-foreground">
                {state.currentItem[field.name] || '-'}
              </span>
            </div>
          ))}
        </div>
      )}

      <div className="flex gap-2">
        <Button
          onClick={() => actions.setMode('edit', state.selectedId)}
        >
          Edit
        </Button>
        <Button
          onClick={() => actions.setMode('list')}
          variant="outline"
        >
          Back to List
        </Button>
      </div>
    </div>
  )
}

// ============================================
// EXPORTS
// ============================================

Crud.Toolbar = CrudToolbar
Crud.Filters = CrudFilters
Crud.List = CrudList
Crud.Form = CrudForm
Crud.View = CrudView

export { Crud, useCrudContext }
export type { CrudProps }

import { useCallback, useEffect, useState } from 'react'
import { useQueryStates, parseAsString, parseAsInteger, parseAsJson } from 'nuqs'
import type { DataProvider, GetListParams, Schema } from '@/lib/crudkit/data-provider'

// ============================================
// TYPES
// ============================================

type Mode = 'list' | 'create' | 'edit' | 'view'

interface CrudState<T = any> {
  mode: Mode
  selectedId: string | null
  page: number
  pageSize: number
  sortField: string | null
  sortOrder: 'asc' | 'desc'
  filters: Record<string, string>
  search: string
  data: T[]
  totalCount: number
  loading: boolean
  error: string | null
  selectedRows: string[]
  currentItem: T | null
}

interface CrudActions {
  setMode: (mode: Mode, id?: string | null) => void
  setSort: (field: string) => void
  setPage: (page: number) => void
  setPageSize: (size: number) => void
  setFilter: (key: string, value: string | null) => void
  clearFilters: () => void
  setSearch: (query: string) => void
  selectRows: (ids: string[]) => void
  refresh: () => Promise<void>
  save: (formData: any) => Promise<void>
  delete: (id: string) => Promise<void>
  deleteMany: () => Promise<void>
}

export interface UseCrudReturn<T = any> {
  state: CrudState<T>
  actions: CrudActions
}

// ============================================
// HOOK
// ============================================

/**
 * useCrud Hook
 *
 * Main state management hook for CRUDKit.
 * Handles URL state persistence, data fetching, and CRUD operations.
 *
 * @param schema - Resource schema definition
 * @param dataProvider - Data provider implementation
 * @returns State and actions for CRUD operations
 *
 * @example
 * ```typescript
 * const { state, actions } = useCrud(userSchema, userDataProvider)
 *
 * // Access state
 * console.log(state.data, state.loading)
 *
 * // Perform actions
 * actions.setMode('create')
 * actions.save(formData)
 * ```
 */
export function useCrud<T = any>(
  schema: Schema,
  dataProvider: DataProvider<T>
): UseCrudReturn<T> {
  // ============================================
  // URL STATE (via nuqs)
  // ============================================

  const [urlState, setUrlState] = useQueryStates(
    {
      mode: parseAsString.withDefault('list'),
      id: parseAsString,
      page: parseAsInteger.withDefault(1),
      pageSize: parseAsInteger.withDefault(10),
      sortField: parseAsString,
      sortOrder: parseAsString.withDefault('asc'),
      filters: parseAsJson<Record<string, string>>().withDefault({}),
      search: parseAsString.withDefault(''),
    },
    {
      history: 'replace', // Use replace to avoid cluttering history
    }
  )

  // ============================================
  // LOCAL STATE
  // ============================================

  const [data, setData] = useState<T[]>([])
  const [totalCount, setTotalCount] = useState(0)
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState<string | null>(null)
  const [selectedRows, setSelectedRows] = useState<string[]>([])
  const [currentItem, setCurrentItem] = useState<T | null>(null)

  // ============================================
  // DATA FETCHING
  // ============================================

  const fetchList = useCallback(async () => {
    if (urlState.mode !== 'list') return

    setLoading(true)
    setError(null)

    try {
      const params: GetListParams = {
        pagination: {
          page: urlState.page,
          pageSize: urlState.pageSize,
        },
        sort: urlState.sortField
          ? {
              field: urlState.sortField,
              order: urlState.sortOrder as 'asc' | 'desc',
            }
          : null,
        filters: urlState.filters,
        search: urlState.search,
      }

      const result = await dataProvider.getList(params)

      setData(result.data)
      setTotalCount(result.total)
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch data')
    } finally {
      setLoading(false)
    }
  }, [
    urlState.mode,
    urlState.page,
    urlState.pageSize,
    urlState.sortField,
    urlState.sortOrder,
    urlState.filters,
    urlState.search,
    dataProvider,
  ])

  const fetchItem = useCallback(async () => {
    if (!urlState.id || !['view', 'edit'].includes(urlState.mode)) return

    setLoading(true)
    setError(null)

    try {
      const item = await dataProvider.getOne(urlState.id)
      setCurrentItem(item)
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch item')
    } finally {
      setLoading(false)
    }
  }, [urlState.id, urlState.mode, dataProvider])

  // ============================================
  // EFFECTS
  // ============================================

  useEffect(() => {
    fetchList()
  }, [fetchList])

  useEffect(() => {
    fetchItem()
  }, [fetchItem])

  // Reset currentItem when entering create mode
  useEffect(() => {
    if (urlState.mode === 'create') {
      setCurrentItem({} as T)
    }
  }, [urlState.mode])

  // ============================================
  // ACTIONS
  // ============================================

  const actions: CrudActions = {
    setMode: (newMode: Mode, id: string | null = null) => {
      setUrlState({ mode: newMode, id })
    },

    setSort: (field: string) => {
      if (urlState.sortField === field) {
        // Toggle order if same field
        setUrlState({
          sortOrder: urlState.sortOrder === 'asc' ? 'desc' : 'asc',
        })
      } else {
        // Set new field with asc order
        setUrlState({
          sortField: field,
          sortOrder: 'asc',
        })
      }
    },

    setPage: (newPage: number) => {
      setUrlState({ page: newPage })
    },

    setPageSize: (newSize: number) => {
      setUrlState({ pageSize: newSize, page: 1 }) // Reset to first page
    },

    setFilter: (key: string, value: string | null) => {
      setUrlState((prev) => ({
        filters: {
          ...prev.filters,
          [key]: value || undefined, // Remove if null/empty
        },
        page: 1, // Reset to first page
      }))
    },

    clearFilters: () => {
      setUrlState({ filters: {}, page: 1 })
    },

    setSearch: (query: string) => {
      setUrlState({ search: query, page: 1 })
    },

    selectRows: (ids: string[]) => {
      setSelectedRows(ids)
    },

    refresh: async () => {
      await fetchList()
    },

    save: async (formData: any) => {
      setLoading(true)
      setError(null)

      try {
        if (urlState.mode === 'create') {
          await dataProvider.create(formData)
        } else if (urlState.mode === 'edit' && urlState.id) {
          await dataProvider.update(urlState.id, formData)
        }

        // Return to list and refresh
        setUrlState({ mode: 'list', id: null })
        await fetchList()
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to save')
        throw err // Re-throw to let form handle it
      } finally {
        setLoading(false)
      }
    },

    delete: async (id: string) => {
      if (!window.confirm('Are you sure you want to delete this item?')) {
        return
      }

      setLoading(true)
      setError(null)

      try {
        await dataProvider.delete(id)
        await fetchList()
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to delete')
      } finally {
        setLoading(false)
      }
    },

    deleteMany: async () => {
      if (selectedRows.length === 0) return

      if (
        !window.confirm(
          `Are you sure you want to delete ${selectedRows.length} item(s)?`
        )
      ) {
        return
      }

      setLoading(true)
      setError(null)

      try {
        await dataProvider.deleteMany(selectedRows)
        setSelectedRows([])
        await fetchList()
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to delete items')
      } finally {
        setLoading(false)
      }
    },
  }

  // ============================================
  // RETURN
  // ============================================

  const state: CrudState<T> = {
    mode: urlState.mode as Mode,
    selectedId: urlState.id,
    page: urlState.page,
    pageSize: urlState.pageSize,
    sortField: urlState.sortField,
    sortOrder: urlState.sortOrder as 'asc' | 'desc',
    filters: urlState.filters,
    search: urlState.search,
    data,
    totalCount,
    loading,
    error,
    selectedRows,
    currentItem,
  }

  return { state, actions }
}

/**
 * CRUDKit Data Provider Interface
 *
 * Implement this interface to connect CRUDKit to your data source.
 * Supports REST APIs, GraphQL, local storage, or any custom backend.
 */

// ============================================
// TYPE DEFINITIONS
// ============================================

/**
 * Field configuration for a schema
 */
export interface Field {
  /** Field name (must match data property) */
  name: string

  /** Human-readable label */
  label: string

  /** Input type (text, email, select, textarea, etc.) */
  type?: string

  /** Is this field required? */
  required?: boolean

  /** Can this field be sorted? */
  sortable?: boolean

  /** Can this field be filtered? */
  filterable?: boolean

  /** Show on create form? */
  showOnCreate?: boolean

  /** Show on edit form? */
  showOnEdit?: boolean

  /** Options for select/radio inputs */
  options?: Array<{ value: string; label: string }>
}

/**
 * Schema definition for a resource
 */
export interface Schema {
  /** Resource title (e.g., "User", "Product") */
  title: string

  /** Name of the ID field (default: "id") */
  idField: string

  /** Field definitions */
  fields: Field[]
}

/**
 * Pagination parameters
 */
export interface PaginationParams {
  /** Current page number (1-indexed) */
  page: number

  /** Items per page */
  pageSize: number
}

/**
 * Sort parameters
 */
export interface SortParams {
  /** Field to sort by */
  field: string

  /** Sort order */
  order: 'asc' | 'desc'
}

/**
 * Filter parameters (field -> value mapping)
 */
export type FilterParams = Record<string, any>

/**
 * Parameters for getList operation
 */
export interface GetListParams {
  /** Pagination settings */
  pagination: PaginationParams

  /** Sort settings (optional) */
  sort?: SortParams | null

  /** Filter settings (optional) */
  filters?: FilterParams

  /** Search query (optional) */
  search?: string
}

/**
 * Result from getList operation
 */
export interface GetListResult<T = any> {
  /** Array of data items */
  data: T[]

  /** Total count of items (for pagination) */
  total: number

  /** Current page number */
  page: number

  /** Items per page */
  pageSize: number
}

// ============================================
// DATA PROVIDER INTERFACE
// ============================================

/**
 * Data Provider Interface
 *
 * Implement this interface to connect your data source to CRUDKit.
 *
 * @example
 * ```typescript
 * class MyDataProvider implements DataProvider {
 *   async getList(params: GetListParams) {
 *     const response = await fetch('/api/users?' + new URLSearchParams({
 *       page: String(params.pagination.page),
 *       pageSize: String(params.pagination.pageSize),
 *       // ... other params
 *     }))
 *     return response.json()
 *   }
 *
 *   async getOne(id: string) {
 *     const response = await fetch(`/api/users/${id}`)
 *     return response.json()
 *   }
 *
 *   // ... implement other methods
 * }
 * ```
 */
export interface DataProvider<T = any> {
  /**
   * Fetch a paginated list of items
   *
   * @param params - Pagination, sorting, filtering, and search parameters
   * @returns Paginated list result
   */
  getList(params: GetListParams): Promise<GetListResult<T>>

  /**
   * Fetch a single item by ID
   *
   * @param id - Item identifier
   * @returns Single item
   */
  getOne(id: string): Promise<T>

  /**
   * Create a new item
   *
   * @param data - Item data
   * @returns Created item
   */
  create(data: Partial<T>): Promise<T>

  /**
   * Update an existing item
   *
   * @param id - Item identifier
   * @param data - Updated fields
   * @returns Updated item
   */
  update(id: string, data: Partial<T>): Promise<T>

  /**
   * Delete a single item
   *
   * @param id - Item identifier
   */
  delete(id: string): Promise<void>

  /**
   * Delete multiple items
   *
   * @param ids - Array of item identifiers
   */
  deleteMany(ids: string[]): Promise<void>
}

// ============================================
// BASE DATA PROVIDER (OPTIONAL)
// ============================================

/**
 * Base Data Provider Class
 *
 * Optional base class with common utilities.
 * Extend this class to reduce boilerplate.
 *
 * @example
 * ```typescript
 * class RestDataProvider extends BaseDataProvider {
 *   constructor(private baseUrl: string) {
 *     super()
 *   }
 *
 *   async getList(params: GetListParams) {
 *     // Implement using this.baseUrl
 *   }
 *
 *   // ... implement other methods
 * }
 * ```
 */
export abstract class BaseDataProvider<T = any> implements DataProvider<T> {
  abstract getList(params: GetListParams): Promise<GetListResult<T>>
  abstract getOne(id: string): Promise<T>
  abstract create(data: Partial<T>): Promise<T>
  abstract update(id: string, data: Partial<T>): Promise<T>
  abstract delete(id: string): Promise<void>
  abstract deleteMany(ids: string[]): Promise<void>

  /**
   * Helper: Build query string from params
   */
  protected buildQueryString(params: GetListParams): string {
    const queryParams = new URLSearchParams()

    // Pagination
    queryParams.set('page', String(params.pagination.page))
    queryParams.set('pageSize', String(params.pagination.pageSize))

    // Sort
    if (params.sort) {
      queryParams.set('sortField', params.sort.field)
      queryParams.set('sortOrder', params.sort.order)
    }

    // Filters
    if (params.filters) {
      Object.entries(params.filters).forEach(([key, value]) => {
        if (value !== null && value !== undefined && value !== '') {
          queryParams.set(`filter[${key}]`, String(value))
        }
      })
    }

    // Search
    if (params.search) {
      queryParams.set('search', params.search)
    }

    return queryParams.toString()
  }

  /**
   * Helper: Handle API errors
   */
  protected async handleResponse<R>(response: Response): Promise<R> {
    if (!response.ok) {
      const errorText = await response.text()
      throw new Error(`API Error: ${response.status} - ${errorText}`)
    }
    return response.json()
  }
}
