'use client'

import React, { createContext, useContext, useState, useEffect, useMemo, useCallback } from 'react'
import { useCrud, type UseCrudReturn } from '@/hooks/use-crud'
import type { DataProvider, Schema, Field } from '@/lib/crudkit/data-provider'
import { cn } from '@/lib/utils'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { Textarea } from '@/components/ui/textarea'
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table'
import { Checkbox } from '@/components/ui/checkbox'
import { Badge } from '@/components/ui/badge'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Skeleton } from '@/components/ui/skeleton'
import {
  Row as DefaultRow,
  Cell as DefaultCell,
  Actions as DefaultActions,
  Header as DefaultHeader,
  EmptyState as DefaultEmptyState,
  LoadingState as DefaultLoadingState,
} from './table'
import { Toolbar } from './toolbar'
import { Field as DefaultField, FormLayout as DefaultFormLayout, SubmitButton as DefaultSubmitButton } from './form'
import { ViewField as DefaultViewField, ViewLayout as DefaultViewLayout } from './view'
import { Filter as DefaultFilter, FilterInput as DefaultFilterInput, FilterLayout as DefaultFilterLayout } from './filters'
import type { CrudListProps, CrudFormProps as CrudFormPropsType, CrudViewProps, CrudFiltersProps } from '@/lib/crudkit/types'

// ============================================
// CONTEXT
// ============================================

interface CrudContextValue extends UseCrudReturn {
  schema: Schema
}

const CrudContext = createContext<CrudContextValue | null>(null)

function useCrudContext<T = any>(): CrudContextValue {
  const context = useContext(CrudContext)
  if (!context) {
    throw new Error('Crud compound components must be used within <Crud>')
  }
  return context
}

// ============================================
// MAIN CRUD COMPONENT
// ============================================

interface CrudProps {
  schema: Schema
  dataProvider: DataProvider
  children: React.ReactNode
}

function Crud({ schema, dataProvider, children }: CrudProps) {
  const crudReturn = useCrud(schema, dataProvider)

  return (
    <CrudContext.Provider value={{ ...crudReturn, schema }}>
      <div className={cn('crud-container', 'p-4')}>{children}</div>
    </CrudContext.Provider>
  )
}

// ============================================
// TOOLBAR COMPONENT
// ============================================
// Toolbar is now a compound component imported from ./toolbar
// with sub-components: Toolbar.Title, Toolbar.CreateButton, Toolbar.RefreshButton

// ============================================
// FILTERS COMPONENT
// ============================================

const CrudFilters = React.memo(({ filterFields, className, components = {} }: CrudFiltersProps) => {
  const { schema, state, actions } = useCrudContext()

  // Use custom components or defaults
  const Filter = components.Filter ?? DefaultFilter
  const Layout = components.Layout ?? DefaultFilterLayout

  const fields = useMemo(
    () => filterFields || schema.fields.filter((f) => f.filterable),
    [filterFields, schema.fields]
  )

  const handleClearFilters = useCallback(() => {
    actions.clearFilters()
  }, [actions])

  const hasActiveFilters = useMemo(
    () => Object.values(state.filters).some((value) => value !== null && value !== ''),
    [state.filters]
  )

  if (state.mode !== 'list') return null
  if (fields.length === 0) return null

  return (
    <Layout
      onClearFilters={handleClearFilters}
      hasActiveFilters={hasActiveFilters}
      schema={schema}
      actions={actions}
      state={state}
      className={className}
    >
      {fields.map((field) => {
        // Use field-specific renderer if provided
        const FilterComponent = components.filters?.[field.name] ?? Filter

        return (
          <FilterComponent
            key={field.name}
            field={field}
            value={state.filters[field.name] || ''}
            onChange={(value) => actions.setFilter(field.name, value || null)}
            onClear={() => actions.setFilter(field.name, null)}
            schema={schema}
            actions={actions}
            state={state}
          />
        )
      })}
    </Layout>
  )
})
CrudFilters.displayName = 'CrudFilters'

// ============================================
// LIST COMPONENT
// ============================================

const CrudList = React.memo(<T,>({ columns, showActions = true, className, components = {} }: CrudListProps<T>) => {
  const { schema, state, actions } = useCrudContext<T>()

  // Use custom components or defaults
  const Row = components.Row ?? DefaultRow
  const Cell = components.Cell ?? DefaultCell
  const Actions = components.Actions ?? DefaultActions
  const Header = components.Header ?? DefaultHeader
  const EmptyState = components.EmptyState ?? DefaultEmptyState
  const LoadingState = components.LoadingState ?? DefaultLoadingState

  const displayColumns = useMemo(
    () => columns || schema.fields.map((f) => f.name),
    [columns, schema.fields]
  )

  const visibleFields = useMemo(
    () => schema.fields.filter((f) => displayColumns.includes(f.name)),
    [schema.fields, displayColumns]
  )

  const handleSearchChange = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    actions.setSearch(e.target.value)
  }, [actions])

  const handleClearSearch = useCallback(() => {
    actions.setSearch('')
  }, [actions])

  const handleDeleteMany = useCallback(() => {
    actions.deleteMany()
  }, [actions])

  const handleClearSelection = useCallback(() => {
    actions.selectRows([])
  }, [actions])

  const handleSelectAll = useCallback((checked: boolean) => {
    if (checked) {
      actions.selectRows(state.data.map((item: any) => item[schema.idField]))
    } else {
      actions.selectRows([])
    }
  }, [actions, state.data, schema.idField])

  const handleSort = useCallback((col: string) => {
    actions.setSort(col)
  }, [actions])

  const handleView = useCallback((id: string | number) => {
    actions.setMode('view', String(id))
  }, [actions])

  const handleEdit = useCallback((id: string | number) => {
    actions.setMode('edit', String(id))
  }, [actions])

  const handleDelete = useCallback((id: string | number) => {
    actions.delete(String(id))
  }, [actions])

  const handleRowSelect = useCallback((id: string | number, checked: boolean) => {
    if (checked) {
      actions.selectRows([...state.selectedRows, String(id)])
    } else {
      actions.selectRows(state.selectedRows.filter((rowId) => rowId !== String(id)))
    }
  }, [actions, state.selectedRows])

  const handlePageChange = useCallback((page: number) => {
    actions.setPage(page)
  }, [actions])

  const handlePageSizeChange = useCallback((value: string) => {
    actions.setPageSize(Number(value))
  }, [actions])

  if (state.mode !== 'list') return null

  return (
    <div className={cn('crud-list', className)}>
      {/* Search Bar */}
      <div className={cn('mb-4 flex gap-2')}>
        <Input
          type="text"
          placeholder="Search..."
          value={state.search}
          onChange={handleSearchChange}
          className="w-80"
        />
        {state.search && (
          <Button
            onClick={handleClearSearch}
            variant="outline"
          >
            Clear
          </Button>
        )}
      </div>

      {/* Bulk Actions */}
      {state.selectedRows.length > 0 && (
        <div className={cn('mb-4 rounded bg-muted p-3 flex items-center gap-4')}>
          <span>
            {state.selectedRows.length} items selected
          </span>
          <Button
            onClick={handleDeleteMany}
            variant="destructive"
            size="sm"
          >
            Delete Selected
          </Button>
          <Button
            onClick={handleClearSelection}
            variant="ghost"
            size="sm"
          >
            Clear Selection
          </Button>
        </div>
      )}

      {/* Error Display */}
      {state.error && (
        <Alert variant="destructive" className="mb-4">
          <AlertDescription>{state.error}</AlertDescription>
        </Alert>
      )}

      {/* Table */}
      <Table>
        <TableHeader>
          <TableRow>
            <TableHead className="w-[50px]">
              <Checkbox
                checked={
                  state.selectedRows.length === state.data.length &&
                  state.data.length > 0
                }
                onCheckedChange={handleSelectAll}
              />
            </TableHead>
            {visibleFields.map((field) => (
              <Header
                key={field.name}
                field={field}
                sortable={field.sortable !== false}
                currentSort={
                  state.sortField === field.name
                    ? { field: state.sortField, order: state.sortOrder as 'asc' | 'desc' }
                    : null
                }
                onSort={handleSort}
                schema={schema}
                actions={actions}
                state={state}
              />
            ))}
            {showActions && <TableHead>Actions</TableHead>}
          </TableRow>
        </TableHeader>
        <TableBody>
          {state.loading && (
            <LoadingState
              rowCount={state.pageSize}
              schema={schema}
              actions={actions}
              state={state}
            />
          )}
          {!state.loading && state.data.length === 0 && (
            <EmptyState
              hasFilters={Object.keys(state.filters).length > 0 || state.search !== ''}
              onClearFilters={() => {
                actions.clearFilters()
                actions.setSearch('')
              }}
              schema={schema}
              actions={actions}
              state={state}
            />
          )}
          {!state.loading &&
            state.data.map((item: any, index: number) => {
              const itemId = item[schema.idField]
              return (
                <Row
                  key={itemId}
                  row={item}
                  index={index}
                  selected={state.selectedRows.includes(String(itemId))}
                  onSelect={(checked) => handleRowSelect(itemId, checked)}
                  onView={() => handleView(itemId)}
                  onEdit={() => handleEdit(itemId)}
                  onDelete={() => handleDelete(itemId)}
                  schema={schema}
                  actions={actions}
                  state={state}
                  visibleColumns={displayColumns}
                  showActions={showActions}
                  CellComponent={Cell}
                  ActionsComponent={Actions}
                />
              )
            })}
        </TableBody>
      </Table>

      {/* Pagination */}
      <div
        className={cn(
          'mt-4 flex items-center justify-between rounded bg-muted p-4'
        )}
      >
        <div className={cn('flex items-center gap-4')}>
          <span className="text-sm">
            Showing {Math.min((state.page - 1) * state.pageSize + 1, state.totalCount)} to{' '}
            {Math.min(state.page * state.pageSize, state.totalCount)} of{' '}
            {state.totalCount} items
          </span>
          <Select
            value={String(state.pageSize)}
            onValueChange={handlePageSizeChange}
          >
            <SelectTrigger className="w-[140px]">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="5">5 per page</SelectItem>
              <SelectItem value="10">10 per page</SelectItem>
              <SelectItem value="25">25 per page</SelectItem>
              <SelectItem value="50">50 per page</SelectItem>
            </SelectContent>
          </Select>
        </div>
        <div className={cn('flex items-center gap-1')}>
          <Button
            onClick={() => handlePageChange(1)}
            disabled={state.page === 1}
            variant="outline"
            size="sm"
          >
            ⏮
          </Button>
          <Button
            onClick={() => handlePageChange(state.page - 1)}
            disabled={state.page === 1}
            variant="outline"
            size="sm"
          >
            ← Previous
          </Button>
          <Button
            onClick={() => handlePageChange(state.page + 1)}
            disabled={state.page * state.pageSize >= state.totalCount}
            variant="outline"
            size="sm"
          >
            Next →
          </Button>
          <Button
            onClick={() => handlePageChange(Math.ceil(state.totalCount / state.pageSize))}
            disabled={state.page * state.pageSize >= state.totalCount}
            variant="outline"
            size="sm"
          >
            ⏭
          </Button>
        </div>
      </div>
    </div>
  )
}) as <T = any>(props: CrudListProps<T>) => React.ReactElement | null
CrudList.displayName = 'CrudList'

// ============================================
// FORM COMPONENT
// ============================================

const CrudForm = React.memo(({ fields, className, components = {} }: CrudFormPropsType) => {
  const { schema, state, actions } = useCrudContext()
  const [formData, setFormData] = useState<Record<string, unknown>>({})
  const [errors, setErrors] = useState<Record<string, string>>({})

  // Use custom components or defaults
  const Field = components.Field ?? DefaultField
  const Layout = components.Layout ?? DefaultFormLayout
  const SubmitButton = components.SubmitButton ?? DefaultSubmitButton

  useEffect(() => {
    if (state.mode === 'edit' && state.currentItem) {
      setFormData(state.currentItem)
    } else if (state.mode === 'create') {
      setFormData({})
    }
  }, [state.mode, state.currentItem])

  const displayFields = useMemo(
    () => fields ||
      schema.fields.filter((f) =>
        state.mode === 'create' ? f.showOnCreate !== false : f.showOnEdit !== false
      ),
    [fields, schema.fields, state.mode]
  )

  const handleSubmit = useCallback(async (e: React.FormEvent) => {
    e.preventDefault()

    // Validation
    const newErrors: Record<string, string> = {}
    displayFields.forEach((field) => {
      if (field.required && !formData[field.name]) {
        newErrors[field.name] = `${field.label} is required`
      }
    })

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors)
      return
    }

    try {
      await actions.save(formData)
      setFormData({})
      setErrors({})
    } catch (err) {
      // Error already handled in useCrud
    }
  }, [displayFields, formData, actions])

  const handleChange = useCallback((field: string, value: unknown) => {
    setFormData((prev) => ({ ...prev, [field]: value }))
    setErrors((prev) => ({ ...prev, [field]: '' }))
  }, [])

  const handleCancel = useCallback(() => {
    actions.setMode('list')
  }, [actions])

  if (!['create', 'edit'].includes(state.mode)) return null

  return (
    <Layout
      title={`${state.mode === 'create' ? 'Create' : 'Edit'} ${schema.title}`}
      error={state.error}
      onSubmit={handleSubmit}
      onCancel={handleCancel}
      isSubmitting={state.loading}
      mode={state.mode as 'create' | 'edit'}
      schema={schema}
      actions={actions}
      state={state}
      className={className}
    >
      {displayFields.map((field) => {
        // Use field-specific renderer if provided
        const FieldComponent = components.fields?.[field.name] ?? Field

        return (
          <FieldComponent
            key={field.name}
            field={field}
            value={formData[field.name]}
            error={errors[field.name]}
            onChange={(value) => handleChange(field.name, value)}
            onBlur={() => {/* optional field blur handler */}}
            disabled={state.loading}
            mode={state.mode as 'create' | 'edit'}
            schema={schema}
            actions={actions}
            state={state}
          />
        )
      })}
    </Layout>
  )
})
CrudForm.displayName = 'CrudForm'

// ============================================
// VIEW COMPONENT
// ============================================

const CrudView = React.memo(({ className, components = {} }: CrudViewProps) => {
  const { schema, state, actions } = useCrudContext()

  // Use custom components or defaults
  const Field = components.Field ?? DefaultViewField
  const Layout = components.Layout ?? DefaultViewLayout

  const handleEdit = useCallback(() => {
    actions.setMode('edit', state.selectedId)
  }, [actions, state.selectedId])

  const handleBackToList = useCallback(() => {
    actions.setMode('list')
  }, [actions])

  if (state.mode !== 'view' || !state.currentItem) return null

  return (
    <Layout
      item={state.currentItem}
      onEdit={handleEdit}
      onBack={handleBackToList}
      schema={schema}
      actions={actions}
      state={state}
      className={className}
    >
      {schema.fields.map((field) => {
        // Use field-specific renderer if provided
        const FieldComponent = components.fields?.[field.name] ?? Field

        return (
          <FieldComponent
            key={field.name}
            field={field}
            value={state.currentItem[field.name]}
            item={state.currentItem}
            schema={schema}
            actions={actions}
            state={state}
          />
        )
      })}
    </Layout>
  )
})
CrudView.displayName = 'CrudView'

// ============================================
// EXPORTS
// ============================================

Crud.Toolbar = Toolbar
Crud.Filters = CrudFilters
Crud.List = CrudList
Crud.Form = CrudForm
Crud.View = CrudView

export { Crud, useCrudContext }
export type { CrudProps }

import * as React from 'react'
import { TableRow, TableCell } from '@/components/ui/table'
import { Checkbox } from '@/components/ui/checkbox'
import { cn } from '@/lib/utils'
import type { RowProps } from '@/lib/crudkit/types'
import { Cell as DefaultCell } from '.'
import { Actions as DefaultActions } from '.'

/**
 * Default Row Component
 *
 * Renders a complete table row with selection checkbox, cells, and actions.
 * This is the most complex default component as it orchestrates the entire row.
 *
 * @example
 * ```typescript
 * const ExpandableRow: React.FC<RowProps<User>> = (props) => {
 *   const [expanded, setExpanded] = useState(false)
 *   return (
 *     <>
 *       <TableRow onClick={() => setExpanded(!expanded)}>
 *         <TableCell><Checkbox checked={props.selected} onCheckedChange={props.onSelect} /></TableCell>
 *         <TableCell>{props.row.name}</TableCell>
 *         <TableCell>{props.row.email}</TableCell>
 *       </TableRow>
 *       {expanded && <TableRow><TableCell colSpan={999}>Expanded details...</TableCell></TableRow>}
 *     </>
 *   )
 * }
 * ```
 */
export const DefaultRow = React.forwardRef<HTMLTableRowElement, RowProps & {
  visibleColumns: string[]
  showActions: boolean
  CellComponent: React.ComponentType<any>
  ActionsComponent: React.ComponentType<any>
}>(
  ({
    row,
    index,
    selected,
    onSelect,
    onView,
    onEdit,
    onDelete,
    className,
    schema,
    actions,
    state,
    visibleColumns,
    showActions,
    CellComponent,
    ActionsComponent
  }, ref) => {
    const itemId = row[schema.idField]

    return (
      <TableRow
        ref={ref}
        className={cn(className)}
        data-state={selected ? 'selected' : undefined}
      >
        <TableCell>
          <Checkbox
            checked={selected}
            onCheckedChange={(checked) => onSelect(!!checked)}
          />
        </TableCell>
        {visibleColumns.map((col) => {
          const field = schema.fields.find((f) => f.name === col)
          if (!field) return null

          return (
            <CellComponent
              key={col}
              value={row[col]}
              row={row}
              field={field}
              column={col}
              schema={schema}
              actions={actions}
              state={state}
            />
          )
        })}
        {showActions && (
          <ActionsComponent
            row={row}
            onView={onView}
            onEdit={onEdit}
            onDelete={onDelete}
            schema={schema}
            actions={actions}
            state={state}
          />
        )}
      </TableRow>
    )
  }
)
DefaultRow.displayName = 'DefaultRow'

import * as React from 'react'
import { TableCell } from '@/components/ui/table'
import { cn } from '@/lib/utils'
import type { CellProps } from '@/lib/crudkit/types'

/**
 * Default Cell Component
 *
 * Renders a table cell with the field value.
 * Override this component to customize cell rendering globally,
 * or use it as a fallback in your custom cell component.
 *
 * @example
 * ```typescript
 * import { DefaultCell } from '@/registry/default/crudkit/crudkit/components/table'
 *
 * const MyCell: React.FC<CellProps> = (props) => {
 *   if (props.field.name === 'status') {
 *     return <TableCell><Badge>{props.value}</Badge></TableCell>
 *   }
 *   // Use default for other fields
 *   return <DefaultCell {...props} />
 * }
 * ```
 */
export const DefaultCell = React.forwardRef<HTMLTableCellElement, CellProps>(
  ({ value, field, row, column, className, schema, actions, state }, ref) => {
    return (
      <TableCell ref={ref} className={cn(className)}>
        {value ?? '-'}
      </TableCell>
    )
  }
)
DefaultCell.displayName = 'DefaultCell'

import * as React from 'react'
import { TableHead } from '@/components/ui/table'
import { cn } from '@/lib/utils'
import type { HeaderProps } from '@/lib/crudkit/types'

/**
 * Default Header Component
 *
 * Renders a sortable column header.
 * Shows sort indicators (↑/↓) when column is sorted.
 *
 * @example
 * ```typescript
 * const MyHeader: React.FC<HeaderProps> = (props) => (
 *   <TableHead onClick={() => props.sortable && props.onSort(props.field.name)}>
 *     <div className="flex items-center gap-2">
 *       <Icon name={props.field.name} />
 *       {props.field.label}
 *       {props.currentSort?.field === props.field.name && (
 *         props.currentSort.order === 'asc' ? '↑' : '↓'
 *       )}
 *     </div>
 *   </TableHead>
 * )
 * ```
 */
export const DefaultHeader = React.forwardRef<HTMLTableCellElement, HeaderProps>(
  ({ field, sortable, currentSort, onSort, className, schema, actions, state }, ref) => {
    const isSorted = currentSort?.field === field.name
    const canSort = sortable && field.sortable !== false

    return (
      <TableHead
        ref={ref}
        className={cn(
          canSort && 'cursor-pointer select-none hover:bg-muted/50',
          className
        )}
        onClick={() => canSort && onSort(field.name)}
      >
        <div className="flex items-center gap-1">
          {field.label}
          {isSorted && (
            <span className="text-muted-foreground">
              {currentSort.order === 'asc' ? '↑' : '↓'}
            </span>
          )}
        </div>
      </TableHead>
    )
  }
)
DefaultHeader.displayName = 'DefaultHeader'

import * as React from 'react'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Textarea } from '@/components/ui/textarea'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { cn } from '@/lib/utils'
import type { FieldProps } from '@/lib/crudkit/types'

/**
 * Default Field Component
 *
 * Renders form inputs based on field type (text, select, textarea, etc.).
 * Override this for global field customization or use fieldRenderers for specific fields.
 *
 * @example
 * ```typescript
 * import { DefaultField } from '@/registry/default/crudkit/crudkit/components/form'
 *
 * // Extend default field
 * const MyField: React.FC<FieldProps> = (props) => {
 *   if (props.field.type === 'custom') {
 *     return <CustomInput {...props} />
 *   }
 *   return <DefaultField {...props} />
 * }
 *
 * // Use with Crud.Form
 * <Crud.Form components={{ Field: MyField }} />
 * ```
 */
export const DefaultField = React.forwardRef<HTMLDivElement, FieldProps>(
  ({ field, value, error, onChange, onBlur, disabled, mode, className, schema, actions, state }, ref) => {
    return (
      <div ref={ref} className={cn('space-y-2', className)}>
        <Label htmlFor={`form-${field.name}`}>
          {field.label}
          {field.required && <span className="text-destructive ml-1">*</span>}
        </Label>

        {field.type === 'select' ? (
          <Select
            value={value ? String(value) : undefined}
            onValueChange={onChange}
            disabled={disabled}
          >
            <SelectTrigger id={`form-${field.name}`}>
              <SelectValue placeholder={`Select ${field.label}`} />
            </SelectTrigger>
            <SelectContent>
              {field.options?.map((option) => (
                <SelectItem key={option.value} value={option.value}>
                  {option.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        ) : field.type === 'textarea' ? (
          <Textarea
            id={`form-${field.name}`}
            value={String(value || '')}
            onChange={(e) => onChange(e.target.value)}
            onBlur={onBlur}
            disabled={disabled}
            rows={4}
          />
        ) : (
          <Input
            id={`form-${field.name}`}
            type={field.type || 'text'}
            value={String(value || '')}
            onChange={(e) => onChange(e.target.value)}
            onBlur={onBlur}
            disabled={disabled}
          />
        )}

        {error && (
          <p className="text-sm text-destructive">
            {error}
          </p>
        )}
      </div>
    )
  }
)
DefaultField.displayName = 'DefaultField'

import * as React from 'react'
import { Label } from '@/components/ui/label'
import { cn } from '@/lib/utils'
import { FilterInput as DefaultFilterInput } from './filter-input'
import type { FilterProps } from '@/lib/crudkit/types'

/**
 * Default Filter Component
 *
 * Displays a single filter with label and input.
 * Override this to customize the filter layout.
 *
 * @example
 * ```typescript
 * import { DefaultFilter } from '@/registry/default/crudkit/crudkit/components/filters'
 * import { Badge } from '@/components/ui/badge'
 *
 * const FilterWithBadge: React.FC<FilterProps> = (props) => {
 *   const { field, value } = props
 *
 *   return (
 *     <div className="relative space-y-2">
 *       <DefaultFilter {...props} />
 *       {value && <Badge className="absolute -top-2 -right-2">Active</Badge>}
 *     </div>
 *   )
 * }
 *
 * <Crud.Filters components={{ Filter: FilterWithBadge }} />
 * ```
 */
export const DefaultFilter = React.forwardRef<HTMLDivElement, FilterProps>(
  ({ field, value, onChange, onClear, className, schema, actions, state }, ref) => {
    return (
      <div ref={ref} className={cn('space-y-2', className)}>
        <Label htmlFor={`filter-${field.name}`}>
          {field.label}
        </Label>
        <DefaultFilterInput
          field={field}
          value={value}
          onChange={onChange}
          schema={schema}
          actions={actions}
          state={state}
        />
      </div>
    )
  }
)
DefaultFilter.displayName = 'DefaultFilter'

import * as React from 'react'
import { Input } from '@/components/ui/input'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { cn } from '@/lib/utils'
import type { FilterInputProps } from '@/lib/crudkit/types'

/**
 * Default FilterInput Component
 *
 * Renders the input element for a filter (text input or select).
 * Override this to customize the input type for all filters.
 *
 * @example
 * ```typescript
 * import { FilterInput as DefaultFilterInput } from '@/registry/default/crudkit/crudkit/components/filters'
 *
 * const CustomFilterInput: React.FC<FilterInputProps> = (props) => {
 *   const { field, value, onChange } = props
 *
 *   // Custom date picker for date fields
 *   if (field.type === 'date') {
 *     return <DatePicker value={value} onChange={onChange} />
 *   }
 *
 *   // Fall back to default for other types
 *   return <DefaultFilterInput {...props} />
 * }
 *
 * <Crud.Filters components={{ FilterInput: CustomFilterInput }} />
 * ```
 */
export const DefaultFilterInput = React.forwardRef<HTMLDivElement, FilterInputProps>(
  ({ field, value, onChange, className, schema, actions, state }, ref) => {
    if (field.type === 'select' && field.options) {
      return (
        <Select
          value={value || '__all__'}
          onValueChange={(newValue) =>
            onChange(newValue === '__all__' ? '' : newValue)
          }
        >
          <SelectTrigger
            ref={ref as any}
            id={`filter-${field.name}`}
            className={cn('w-[180px]', className)}
          >
            <SelectValue placeholder="All" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="__all__">All</SelectItem>
            {field.options.map((option) => (
              <SelectItem key={option.value} value={option.value}>
                {option.label}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      )
    }

    return (
      <Input
        ref={ref as any}
        id={`filter-${field.name}`}
        type="text"
        value={value || ''}
        onChange={(e) => onChange(e.target.value)}
        className={cn('w-[180px]', className)}
      />
    )
  }
)
DefaultFilterInput.displayName = 'DefaultFilterInput'

import * as React from 'react'
import { Slot } from '@radix-ui/react-slot'
import { Button } from '@/components/ui/button'
import { Plus } from 'lucide-react'
import { cn } from '@/lib/utils'
import { useCrudContext } from '../crud'
import type { ToolbarCreateButtonProps } from '@/lib/crudkit/types'

/**
 * Toolbar.CreateButton Component
 *
 * Button to create a new item. Supports asChild pattern for custom elements (e.g., Link).
 *
 * @example
 * ```typescript
 * // Default usage
 * <Crud.Toolbar.CreateButton />
 *
 * // Custom text
 * <Crud.Toolbar.CreateButton>Add New User</Crud.Toolbar.CreateButton>
 *
 * // With custom styling
 * <Crud.Toolbar.CreateButton className="bg-green-500">
 *   <PlusIcon /> Create
 * </Crud.Toolbar.CreateButton>
 *
 * // With asChild (render as Link for client-side routing)
 * <Crud.Toolbar.CreateButton asChild>
 *   <Link href="/users/new">
 *     <PlusIcon className="mr-2" />
 *     Invite Team Member
 *   </Link>
 * </Crud.Toolbar.CreateButton>
 * ```
 */
export const ToolbarCreateButton = React.forwardRef<HTMLButtonElement, ToolbarCreateButtonProps>(
  ({ asChild = false, className, children, onClick }, ref) => {
    const { actions } = useCrudContext()

    const handleClick = (e: React.MouseEvent<HTMLButtonElement>) => {
      onClick?.()
      actions.setMode('create')
    }

    if (asChild) {
      return (
        <Slot ref={ref} onClick={handleClick as any}>
          {children}
        </Slot>
      )
    }

    return (
      <Button
        ref={ref}
        onClick={handleClick}
        className={cn(className)}
        variant="default"
      >
        <Plus className="h-4 w-4 mr-2" />
        {children || 'Create New'}
      </Button>
    )
  }
)
ToolbarCreateButton.displayName = 'Crud.Toolbar.CreateButton'

import * as React from 'react'
import { Slot } from '@radix-ui/react-slot'
import { Button } from '@/components/ui/button'
import { RefreshCw } from 'lucide-react'
import { cn } from '@/lib/utils'
import { useCrudContext } from '../crud'
import type { ToolbarRefreshButtonProps } from '@/lib/crudkit/types'

/**
 * Toolbar.RefreshButton Component
 *
 * Button to refresh/reload data. Supports asChild pattern.
 *
 * @example
 * ```typescript
 * // Default usage
 * <Crud.Toolbar.RefreshButton />
 *
 * // Custom text
 * <Crud.Toolbar.RefreshButton>Reload Data</Crud.Toolbar.RefreshButton>
 *
 * // With custom styling
 * <Crud.Toolbar.RefreshButton className="text-blue-500">
 *   <RefreshIcon /> Refresh
 * </Crud.Toolbar.RefreshButton>
 *
 * // With asChild
 * <Crud.Toolbar.RefreshButton asChild>
 *   <button className="custom-button">
 *     <RefreshIcon /> Reload
 *   </button>
 * </Crud.Toolbar.RefreshButton>
 * ```
 */
export const ToolbarRefreshButton = React.forwardRef<HTMLButtonElement, ToolbarRefreshButtonProps>(
  ({ asChild = false, className, children, onClick }, ref) => {
