import { useCallback, useEffect, useState, useMemo } from 'react'
import { useQueryStates, parseAsString, parseAsInteger, parseAsJson } from 'nuqs'
import type { DataProvider, GetListParams, Schema } from '@/lib/crudkit/data-provider'

// ============================================
// TYPES
// ============================================

type Mode = 'list' | 'create' | 'edit' | 'view'

interface CrudState<T = any> {
  mode: Mode
  selectedId: string | null
  page: number
  pageSize: number
  sortField: string | null
  sortOrder: 'asc' | 'desc'
  filters: Record<string, string>
  search: string
  data: T[]
  totalCount: number
  loading: boolean
  error: string | null
  selectedRows: string[]
  currentItem: T | null
}

interface CrudActions {
  setMode: (mode: Mode, id?: string | null) => void
  setSort: (field: string) => void
  setPage: (page: number) => void
  setPageSize: (size: number) => void
  setFilter: (key: string, value: string | null) => void
  clearFilters: () => void
  setSearch: (query: string) => void
  selectRows: (ids: string[]) => void
  refresh: () => Promise<void>
  save: (formData: Record<string, unknown>) => Promise<void>
  delete: (id: string) => Promise<void>
  deleteMany: () => Promise<void>
}

export interface UseCrudReturn<T = any> {
  state: CrudState<T>
  actions: CrudActions
}

// ============================================
// HOOK
// ============================================

/**
 * useCrud Hook
 *
 * Main state management hook for CRUDKit.
 * Handles URL state persistence, data fetching, and CRUD operations.
 *
 * @param schema - Resource schema definition
 * @param dataProvider - Data provider implementation
 * @returns State and actions for CRUD operations
 *
 * @example
 * ```typescript
 * const { state, actions } = useCrud(userSchema, userDataProvider)
 *
 * // Access state
 * console.log(state.data, state.loading)
 *
 * // Perform actions
 * actions.setMode('create')
 * actions.save(formData)
 * ```
 */
export function useCrud<T = any>(
  schema: Schema,
  dataProvider: DataProvider<T>
): UseCrudReturn<T> {
  // ============================================
  // URL STATE (via nuqs)
  // ============================================

  const [urlState, setUrlState] = useQueryStates(
    {
      mode: parseAsString.withDefault('list'),
      id: parseAsString,
      page: parseAsInteger.withDefault(1),
      pageSize: parseAsInteger.withDefault(10),
      sortField: parseAsString,
      sortOrder: parseAsString.withDefault('asc'),
      filters: parseAsJson<Record<string, string>>((value) => {
        if (typeof value === 'object' && value !== null && !Array.isArray(value)) {
          return value as Record<string, string>
        }
        return null
      }).withDefault({}),
      search: parseAsString.withDefault(''),
    },
    {
      history: 'replace', // Use replace to avoid cluttering history
    }
  )

  // ============================================
  // LOCAL STATE
  // ============================================

  const [data, setData] = useState<T[]>([])
  const [totalCount, setTotalCount] = useState(0)
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState<string | null>(null)
  const [selectedRows, setSelectedRows] = useState<string[]>([])
  const [currentItem, setCurrentItem] = useState<T | null>(null)

  // ============================================
  // DATA FETCHING
  // ============================================

  const fetchList = useCallback(async () => {
    if (urlState.mode !== 'list') return

    setLoading(true)
    setError(null)

    try {
      const params: GetListParams = {
        pagination: {
          page: urlState.page,
          pageSize: urlState.pageSize,
        },
        sort: urlState.sortField
          ? {
              field: urlState.sortField,
              order: urlState.sortOrder as 'asc' | 'desc',
            }
          : null,
        filters: urlState.filters,
        search: urlState.search,
      }

      const result = await dataProvider.getList(params)

      setData(result.data)
      setTotalCount(result.total)
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch data')
    } finally {
      setLoading(false)
    }
  }, [
    urlState.mode,
    urlState.page,
    urlState.pageSize,
    urlState.sortField,
    urlState.sortOrder,
    urlState.filters,
    urlState.search,
    dataProvider,
  ])

  const fetchItem = useCallback(async () => {
    if (!urlState.id || !['view', 'edit'].includes(urlState.mode)) return

    setLoading(true)
    setError(null)

    try {
      const item = await dataProvider.getOne(urlState.id)
      setCurrentItem(item)
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch item')
    } finally {
      setLoading(false)
    }
  }, [urlState.id, urlState.mode, dataProvider])

  // ============================================
  // EFFECTS
  // ============================================

  useEffect(() => {
    fetchList()
  }, [fetchList])

  useEffect(() => {
    fetchItem()
  }, [fetchItem])

  // Reset currentItem when entering create mode
  useEffect(() => {
    if (urlState.mode === 'create') {
      setCurrentItem({} as T)
    }
  }, [urlState.mode])

  // ============================================
  // ACTIONS
  // ============================================

  const setMode = useCallback((newMode: Mode, id: string | null = null) => {
    setUrlState({ mode: newMode, id })
  }, [setUrlState])

  const setSort = useCallback((field: string) => {
    if (urlState.sortField === field) {
      // Toggle order if same field
      setUrlState({
        sortOrder: urlState.sortOrder === 'asc' ? 'desc' : 'asc',
      })
    } else {
      // Set new field with asc order
      setUrlState({
        sortField: field,
        sortOrder: 'asc',
      })
    }
  }, [urlState.sortField, urlState.sortOrder, setUrlState])

  const setPage = useCallback((newPage: number) => {
    setUrlState({ page: newPage })
  }, [setUrlState])

  const setPageSize = useCallback((newSize: number) => {
    setUrlState({ pageSize: newSize, page: 1 }) // Reset to first page
  }, [setUrlState])

  const setFilter = useCallback((key: string, value: string | null) => {
    setUrlState((prev) => {
      const newFilters = { ...prev.filters }
      if (value) {
        newFilters[key] = value
      } else {
        delete newFilters[key]
      }
      return {
        filters: newFilters,
        page: 1, // Reset to first page
      }
    })
  }, [setUrlState])

  const clearFilters = useCallback(() => {
    setUrlState({ filters: {}, page: 1 })
  }, [setUrlState])

  const setSearchAction = useCallback((query: string) => {
    setUrlState({ search: query, page: 1 })
  }, [setUrlState])

  const selectRows = useCallback((ids: string[]) => {
    setSelectedRows(ids)
  }, [])

  const refresh = useCallback(async () => {
    await fetchList()
  }, [fetchList])

  const save = useCallback(async (formData: Record<string, unknown>) => {
    setLoading(true)
    setError(null)

    try {
      if (urlState.mode === 'create') {
        await dataProvider.create(formData)
      } else if (urlState.mode === 'edit' && urlState.id) {
        await dataProvider.update(urlState.id, formData)
      }

      // Return to list and refresh
      setUrlState({ mode: 'list', id: null })
      await fetchList()
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to save')
      throw err // Re-throw to let form handle it
    } finally {
      setLoading(false)
    }
  }, [urlState.mode, urlState.id, dataProvider, setUrlState, fetchList])

  const deleteAction = useCallback(async (id: string) => {
    if (!window.confirm('Are you sure you want to delete this item?')) {
      return
    }

    setLoading(true)
    setError(null)

    try {
      await dataProvider.delete(id)
      await fetchList()
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to delete')
    } finally {
      setLoading(false)
    }
  }, [dataProvider, fetchList])

  const deleteMany = useCallback(async () => {
    if (selectedRows.length === 0) return

    if (
      !window.confirm(
        `Are you sure you want to delete ${selectedRows.length} item(s)?`
      )
    ) {
      return
    }

    setLoading(true)
    setError(null)

    try {
      await dataProvider.deleteMany(selectedRows)
      setSelectedRows([])
      await fetchList()
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to delete items')
    } finally {
      setLoading(false)
    }
  }, [selectedRows, dataProvider, fetchList])

  const actions = useMemo<CrudActions>(() => ({
    setMode,
    setSort,
    setPage,
    setPageSize,
    setFilter,
    clearFilters,
    setSearch: setSearchAction,
    selectRows,
    refresh,
    save,
    delete: deleteAction,
    deleteMany,
  }), [
    setMode,
    setSort,
    setPage,
    setPageSize,
    setFilter,
    clearFilters,
    setSearchAction,
    selectRows,
    refresh,
    save,
    deleteAction,
    deleteMany,
  ])

  // ============================================
  // RETURN
  // ============================================

  const state = useMemo<CrudState<T>>(() => ({
    mode: urlState.mode as Mode,
    selectedId: urlState.id,
    page: urlState.page,
    pageSize: urlState.pageSize,
    sortField: urlState.sortField,
    sortOrder: urlState.sortOrder as 'asc' | 'desc',
    filters: urlState.filters,
    search: urlState.search,
    data,
    totalCount,
    loading,
    error,
    selectedRows,
    currentItem,
  }), [
    urlState.mode,
    urlState.id,
    urlState.page,
    urlState.pageSize,
    urlState.sortField,
    urlState.sortOrder,
    urlState.filters,
    urlState.search,
    data,
    totalCount,
    loading,
    error,
    selectedRows,
    currentItem,
  ])

  return useMemo(() => ({ state, actions }), [state, actions])
}

/**
 * CRUDKit Data Provider Interface
 *
 * Implement this interface to connect CRUDKit to your data source.
 * Supports REST APIs, GraphQL, local storage, or any custom backend.
 */

// ============================================
// TYPE DEFINITIONS
// ============================================

/**
 * Field configuration for a schema
 */
export interface Field {
  /** Field name (must match data property) */
  name: string

  /** Human-readable label */
  label: string

  /** Input type (text, email, select, textarea, etc.) */
  type?: string

  /** Is this field required? */
  required?: boolean

  /** Can this field be sorted? */
  sortable?: boolean

  /** Can this field be filtered? */
  filterable?: boolean

  /** Show on create form? */
  showOnCreate?: boolean

  /** Show on edit form? */
  showOnEdit?: boolean

  /** Options for select/radio inputs */
  options?: Array<{ value: string; label: string }>
}

/**
 * Schema definition for a resource
 */
export interface Schema {
  /** Resource title (e.g., "User", "Product") */
  title: string

  /** Name of the ID field (default: "id") */
  idField: string

  /** Field definitions */
  fields: Field[]
}

/**
 * Pagination parameters
 */
export interface PaginationParams {
  /** Current page number (1-indexed) */
  page: number

  /** Items per page */
  pageSize: number
}

/**
 * Sort parameters
 */
export interface SortParams {
  /** Field to sort by */
  field: string

  /** Sort order */
  order: 'asc' | 'desc'
}

/**
 * Filter parameters (field -> value mapping)
 */
export type FilterParams = Record<string, any>

/**
 * Parameters for getList operation
 */
export interface GetListParams {
  /** Pagination settings */
  pagination: PaginationParams

  /** Sort settings (optional) */
  sort?: SortParams | null

  /** Filter settings (optional) */
  filters?: FilterParams

  /** Search query (optional) */
  search?: string
}

/**
 * Result from getList operation
 */
export interface GetListResult<T = any> {
  /** Array of data items */
  data: T[]

  /** Total count of items (for pagination) */
  total: number

  /** Current page number */
  page: number

  /** Items per page */
  pageSize: number
}

// ============================================
// DATA PROVIDER INTERFACE
// ============================================

/**
 * Data Provider Interface
 *
 * Implement this interface to connect your data source to CRUDKit.
 *
 * @example
 * ```typescript
 * class MyDataProvider implements DataProvider {
 *   async getList(params: GetListParams) {
 *     const response = await fetch('/api/users?' + new URLSearchParams({
 *       page: String(params.pagination.page),
 *       pageSize: String(params.pagination.pageSize),
 *       // ... other params
 *     }))
 *     return response.json()
 *   }
 *
 *   async getOne(id: string) {
 *     const response = await fetch(`/api/users/${id}`)
 *     return response.json()
 *   }
 *
 *   // ... implement other methods
 * }
 * ```
 */
export interface DataProvider<T = any> {
  /**
   * Fetch a paginated list of items
   *
   * @param params - Pagination, sorting, filtering, and search parameters
   * @returns Paginated list result
   */
  getList(params: GetListParams): Promise<GetListResult<T>>

  /**
   * Fetch a single item by ID
   *
   * @param id - Item identifier
   * @returns Single item
   */
  getOne(id: string): Promise<T>

  /**
   * Create a new item
   *
   * @param data - Item data
   * @returns Created item
   */
  create(data: Partial<T>): Promise<T>

  /**
   * Update an existing item
   *
   * @param id - Item identifier
   * @param data - Updated fields
   * @returns Updated item
   */
  update(id: string, data: Partial<T>): Promise<T>

  /**
   * Delete a single item
   *
   * @param id - Item identifier
   */
  delete(id: string): Promise<void>

  /**
   * Delete multiple items
   *
   * @param ids - Array of item identifiers
   */
  deleteMany(ids: string[]): Promise<void>
}

// ============================================
// BASE DATA PROVIDER (OPTIONAL)
// ============================================

/**
 * Base Data Provider Class
 *
 * Optional base class with common utilities.
 * Extend this class to reduce boilerplate.
 *
 * @example
 * ```typescript
 * class RestDataProvider extends BaseDataProvider {
 *   constructor(private baseUrl: string) {
 *     super()
 *   }
 *
 *   async getList(params: GetListParams) {
 *     // Implement using this.baseUrl
 *   }
 *
 *   // ... implement other methods
 * }
 * ```
 */
export abstract class BaseDataProvider<T = any> implements DataProvider<T> {
  abstract getList(params: GetListParams): Promise<GetListResult<T>>
  abstract getOne(id: string): Promise<T>
  abstract create(data: Partial<T>): Promise<T>
  abstract update(id: string, data: Partial<T>): Promise<T>
  abstract delete(id: string): Promise<void>
  abstract deleteMany(ids: string[]): Promise<void>

  /**
   * Helper: Build query string from params
   */
  protected buildQueryString(params: GetListParams): string {
    const queryParams = new URLSearchParams()

    // Pagination
    queryParams.set('page', String(params.pagination.page))
    queryParams.set('pageSize', String(params.pagination.pageSize))

    // Sort
    if (params.sort) {
      queryParams.set('sortField', params.sort.field)
      queryParams.set('sortOrder', params.sort.order)
    }

    // Filters
    if (params.filters) {
      Object.entries(params.filters).forEach(([key, value]) => {
        if (value !== null && value !== undefined && value !== '') {
          queryParams.set(`filter[${key}]`, String(value))
        }
      })
    }

    // Search
    if (params.search) {
      queryParams.set('search', params.search)
    }

    return queryParams.toString()
  }

  /**
   * Helper: Handle API errors
   */
  protected async handleResponse<R>(response: Response): Promise<R> {
    if (!response.ok) {
      const errorText = await response.text()
      throw new Error(`API Error: ${response.status} - ${errorText}`)
    }
    return response.json()
  }
}

/**
 * CRUDKit Component Types
 *
 * Type definitions for customizable component props throughout CRUDKit.
 * These types enable type-safe component overrides via the `components` prop pattern.
 *
 * @example
 * ```typescript
 * import type { CellProps } from '@/registry/default/crudkit/crudkit/lib/component-types'
 *
 * const CustomCell: React.FC<CellProps> = ({ value, field, row }) => {
 *   if (field.name === 'status') {
 *     return <Badge>{value}</Badge>
 *   }
 *   return <TableCell>{value}</TableCell>
 * }
 *
 * <Crud.List components={{ Cell: CustomCell }} />
 * ```
 */

import type * as React from 'react'
import type { Schema, Field } from './data-provider'

// Re-export types from use-crud for convenience
export type { CrudState, CrudActions, UseCrudReturn } from '@/hooks/use-crud'

// ============================================================================
// BASE PROPS - All component props extend this
// ============================================================================

/**
 * Base props provided to all custom components.
 * Contains shared context for accessing schema, state, and actions.
 */
export interface BaseComponentProps<T = any> {
  /** Resource schema definition */
  schema: Schema
  /** CRUD state (data, loading, filters, etc.) */
  state: {
    mode: 'list' | 'create' | 'edit' | 'view'
    selectedId: string | null
    page: number
    pageSize: number
    sortField: string | null
    sortOrder: 'asc' | 'desc'
    filters: Record<string, string>
    search: string
    data: T[]
    totalCount: number
    loading: boolean
    error: string | null
    selectedRows: string[]
    currentItem: T | null
  }
  /** CRUD actions (setMode, save, delete, etc.) */
  actions: {
    setMode: (mode: 'list' | 'create' | 'edit' | 'view', id?: string | null) => void
    setSort: (field: string) => void
    setPage: (page: number) => void
    setPageSize: (size: number) => void
    setFilter: (key: string, value: string | null) => void
    clearFilters: () => void
    setSearch: (query: string) => void
    selectRows: (ids: string[]) => void
    refresh: () => Promise<void>
    save: (formData: Record<string, unknown>) => Promise<void>
    delete: (id: string) => Promise<void>
    deleteMany: () => Promise<void>
  }
  /** Optional className for styling overrides */
  className?: string
}

// ============================================================================
// TABLE COMPONENT PROPS
// ============================================================================

/**
 * Props for custom Row component.
 *
 * @example
 * ```typescript
 * const ExpandableRow: React.FC<RowProps<User>> = ({ row, onView, onEdit }) => {
 *   const [expanded, setExpanded] = useState(false)
 *   return (
 *     <>
 *       <TableRow onClick={() => setExpanded(!expanded)}>
 *         <TableCell>{row.name}</TableCell>
 *         <TableCell>{row.email}</TableCell>
 *       </TableRow>
 *       {expanded && <TableRow>...</TableRow>}
 *     </>
 *   )
 * }
 * ```
 */
export interface RowProps<T = any> extends BaseComponentProps<T> {
  /** The data row being rendered */
  row: T
  /** Row index in current page */
  index: number
  /** Whether this row is selected */
  selected: boolean
  /** Callback when selection changes */
  onSelect: (selected: boolean) => void
  /** Callback to view this row's details */
  onView: () => void
  /** Callback to edit this row */
  onEdit: () => void
  /** Callback to delete this row */
  onDelete: () => void
}

/**
 * Props for custom Cell component.
 *
 * @example
 * ```typescript
 * const StatusCell: React.FC<CellProps> = ({ value, field }) => {
 *   if (field.name === 'status') {
 *     return <Badge variant={value === 'active' ? 'success' : 'destructive'}>{value}</Badge>
 *   }
 *   return <TableCell>{value}</TableCell>
 * }
 * ```
 */
export interface CellProps<T = any> extends BaseComponentProps<T> {
  /** The cell value to display */
  value: any
  /** The entire row data */
  row: T
  /** The field definition for this cell */
  field: Field
  /** The column name */
  column: string
}

/**
 * Props for custom Actions component (row action buttons).
 *
 * @example
 * ```typescript
 * const CustomActions: React.FC<ActionsProps> = ({ row, onView, onEdit, onDelete }) => (
 *   <DropdownMenu>
 *     <DropdownMenuItem onClick={onView}>View</DropdownMenuItem>
 *     <DropdownMenuItem onClick={onEdit}>Edit</DropdownMenuItem>
 *     <DropdownMenuItem onClick={onDelete}>Delete</DropdownMenuItem>
 *   </DropdownMenu>
 * )
 * ```
 */
export interface ActionsProps<T = any> extends BaseComponentProps<T> {
  /** The row data */
  row: T
  /** Callback to view this row */
  onView: () => void
  /** Callback to edit this row */
  onEdit: () => void
  /** Callback to delete this row */
  onDelete: () => void
}

/**
 * Props for custom Header component (column header).
 *
 * @example
 * ```typescript
 * const CustomHeader: React.FC<HeaderProps> = ({ field, sortable, onSort, currentSort }) => (
 *   <TableHead onClick={() => sortable && onSort(field.name)}>
 *     {field.label}
 *     {currentSort?.field === field.name && (currentSort.order === 'asc' ? '↑' : '↓')}
 *   </TableHead>
 * )
 * ```
 */
export interface HeaderProps extends BaseComponentProps {
  /** The field definition for this column */
  field: Field
  /** Whether this column is sortable */
  sortable: boolean
  /** Current sort state (null if not sorted by this column) */
  currentSort: { field: string; order: 'asc' | 'desc' } | null
  /** Callback to sort by this column */
  onSort: (field: string) => void
}

/**
 * Props for custom EmptyState component (shown when no data).
 *
 * @example
 * ```typescript
 * const CustomEmptyState: React.FC<EmptyStateProps> = ({ hasFilters, onClearFilters }) => (
 *   <TableRow>
 *     <TableCell colSpan={999}>
 *       <div className="text-center py-12">
 *         {hasFilters ? (
 *           <>
 *             <p>No results found</p>
 *             <Button onClick={onClearFilters}>Clear filters</Button>
 *           </>
 *         ) : (
 *           <p>No data available</p>
 *         )}
 *       </div>
 *     </TableCell>
 *   </TableRow>
 * )
 * ```
 */
export interface EmptyStateProps extends BaseComponentProps {
  /** Optional custom message */
  message?: string
  /** Whether filters are currently applied */
  hasFilters: boolean
  /** Callback to clear all filters */
  onClearFilters: () => void
}

/**
 * Props for custom LoadingState component (shown during data fetch).
 *
 * @example
 * ```typescript
 * const CustomLoadingState: React.FC<LoadingStateProps> = ({ rowCount = 5 }) => (
 *   <>
 *     {Array.from({ length: rowCount }).map((_, i) => (
 *       <TableRow key={i}>
 *         <TableCell><Skeleton className="h-4 w-full" /></TableCell>
 *       </TableRow>
 *     ))}
 *   </>
 * )
 * ```
 */
export interface LoadingStateProps extends BaseComponentProps {
  /** Number of skeleton rows to show (defaults to pageSize) */
  rowCount?: number
}

// ============================================================================
// FORM COMPONENT PROPS
// ============================================================================

/**
 * Props for custom Field component (form input).
 *
 * @example
 * ```typescript
 * const ImageUploadField: React.FC<FieldProps> = ({ field, value, onChange, error }) => (
 *   <div>
 *     <Label>{field.label}</Label>
 *     <ImageUpload value={value} onChange={onChange} />
 *     {error && <span className="text-destructive">{error}</span>}
 *   </div>
 * )
 *
 * <Crud.Form components={{ fields: { avatar: ImageUploadField } }} />
 * ```
 */
export interface FieldProps extends BaseComponentProps {
  /** The field definition */
  field: Field
  /** Current field value */
  value: any
  /** Validation error message (if any) */
  error?: string
  /** Callback when value changes */
  onChange: (value: any) => void
  /** Callback when field loses focus */
  onBlur: () => void
  /** Whether field is disabled */
  disabled?: boolean
  /** Current form mode */
  mode: 'create' | 'edit'
}

/**
 * Props for custom FormLayout component (wraps entire form).
 *
 * @example
 * ```typescript
 * const WizardFormLayout: React.FC<FormLayoutProps> = ({ children, title, onSubmit, onCancel }) => (
 *   <Card>
 *     <CardHeader><CardTitle>{title}</CardTitle></CardHeader>
 *     <CardContent>
 *       <form onSubmit={onSubmit}>
 *         <div className="grid grid-cols-2 gap-4">{children}</div>
 *         <div className="flex gap-2 mt-4">
 *           <Button type="button" onClick={onCancel}>Cancel</Button>
 *           <Button type="submit">Save</Button>
 *         </div>
 *       </form>
 *     </CardContent>
 *   </Card>
 * )
 * ```
 */
export interface FormLayoutProps extends BaseComponentProps {
  /** Form field elements to render */
  children: React.ReactNode
  /** Form title (e.g., "Create User" or "Edit User") */
  title: string
  /** Form-level error message */
  error?: string | null
  /** Callback when form is submitted */
  onSubmit: () => void
  /** Callback when form is cancelled */
  onCancel: () => void
  /** Whether form is currently submitting */
  isSubmitting: boolean
  /** Current form mode */
  mode: 'create' | 'edit'
}

/**
 * Props for custom SubmitButton component.
 *
 * @example
 * ```typescript
 * const CustomSubmitButton: React.FC<SubmitButtonProps> = ({ isSubmitting, mode }) => (
 *   <Button type="submit" disabled={isSubmitting}>
 *     {isSubmitting ? 'Saving...' : mode === 'create' ? 'Create' : 'Update'}
 *   </Button>
 * )
 * ```
 */
export interface SubmitButtonProps extends BaseComponentProps {
  /** Whether form is currently submitting */
  isSubmitting: boolean
  /** Whether button should be disabled */
  disabled?: boolean
  /** Current form mode */
  mode: 'create' | 'edit'
}

// ============================================================================
// VIEW COMPONENT PROPS
// ============================================================================

/**
 * Props for custom ViewField component (detail view field display).
 *
 * @example
 * ```typescript
 * const AvatarViewField: React.FC<ViewFieldProps<User>> = ({ field, value, item }) => {
 *   if (field.name === 'avatar') {
 *     return (
 *       <div>
 *         <Label>{field.label}</Label>
 *         <Avatar><AvatarImage src={value} /></Avatar>
 *       </div>
 *     )
 *   }
 *   return <div><Label>{field.label}</Label><p>{value}</p></div>
 * }
 * ```
 */
export interface ViewFieldProps<T = any> extends BaseComponentProps<T> {
  /** The field definition */
  field: Field
  /** The field value to display */
  value: any
  /** The entire item being viewed */
  item: T
}

/**
 * Props for custom ViewLayout component (wraps entire detail view).
 *
 * @example
 * ```typescript
 * const CardViewLayout: React.FC<ViewLayoutProps> = ({ children, item, onEdit, onBack }) => (
 *   <Card>
 *     <CardHeader>
 *       <CardTitle>Details</CardTitle>
 *     </CardHeader>
 *     <CardContent>{children}</CardContent>
 *     <CardFooter>
 *       <Button onClick={onBack}>Back</Button>
 *       <Button onClick={onEdit}>Edit</Button>
 *     </CardFooter>
 *   </Card>
 * )
 * ```
 */
export interface ViewLayoutProps<T = any> extends BaseComponentProps<T> {
  /** Field display elements to render */
  children: React.ReactNode
  /** The item being viewed */
  item: T
  /** Callback to switch to edit mode */
  onEdit: () => void
  /** Callback to return to list */
  onBack: () => void
}

// ============================================================================
// KANBAN COMPONENT PROPS
// ============================================================================

/**
 * Props for custom Card component (kanban card).
 *
 * @example
 * ```typescript
 * const PriorityCard: React.FC<CardProps<Task>> = ({ item, isDragging, onView, onEdit }) => (
 *   <Card className={isDragging ? 'opacity-50' : ''}>
 *     <CardHeader>
 *       <CardTitle>{item.title}</CardTitle>
 *       <Badge variant={item.priority === 'high' ? 'destructive' : 'default'}>
 *         {item.priority}
 *       </Badge>
 *     </CardHeader>
 *     <CardContent>{item.description}</CardContent>
 *     <CardFooter>
 *       <Button size="sm" onClick={onView}>View</Button>
 *       <Button size="sm" onClick={onEdit}>Edit</Button>
 *     </CardFooter>
 *   </Card>
 * )
 * ```
 */
export interface CardProps<T = any> extends BaseComponentProps<T> {
  /** The item data for this card */
  item: T
  /** The column this card belongs to */
  column: string
  /** Callback to view item details */
  onView: () => void
  /** Callback to edit item */
  onEdit: () => void
  /** Whether card is currently being dragged */
  isDragging: boolean
}

/**
 * Props for custom ColumnHeader component (kanban column header).
 *
 * @example
 * ```typescript
 * const CustomColumnHeader: React.FC<ColumnHeaderProps> = ({ label, count }) => (
 *   <div className="flex items-center justify-between p-4">
 *     <h3 className="font-semibold">{label}</h3>
 *     <Badge>{count}</Badge>
 *   </div>
 * )
 * ```
 */
export interface ColumnHeaderProps extends BaseComponentProps {
  /** The column identifier */
  column: string
  /** The column display label */
  label: string
  /** Number of items in this column */
  count: number
}

/**
 * Props for custom EmptyColumn component (shown when column has no items).
 *
 * @example
 * ```typescript
 * const CustomEmptyColumn: React.FC<EmptyColumnProps> = ({ label }) => (
 *   <div className="text-center py-8 text-muted-foreground">
 *     No items in {label}
 *   </div>
 * )
 * ```
 */
export interface EmptyColumnProps extends BaseComponentProps {
  /** The column identifier */
  column: string
  /** The column display label */
  label: string
}

// ============================================================================
// TOOLBAR COMPONENT PROPS (Compound Components)
